public class Printthe_halfpyramid_nested_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n =4;
		//outer loop 
		for ( int i=1; i<=n; i++) {
			//inner loop 
			for(int j=1; j<=i; j++) {
				System.out.print("*");
			}
			System.out.println();
			//for the line
		}

	}

}
